mapper.select:
SELECT


,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete
,yundong.yundong_name as yundongName
,yundong.yundong_photo as yundongPhoto
,yundong.yundong_types as yundongTypes
,yundong.yundong_video as yundongVideo
,yundong.yundong_num as yundongNum
,yundong.zan_number as zanNumber
,yundong.cai_number as caiNumber
,yundong.yundong_content as yundongContent
,yundong.yundong_delete as yundongDelete

FROM yundong_collection  a
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
left JOIN yundong yundong ON a.yundong_id = yundong.id



        and a.id in

        #{item}



        and (
            a.yundong_id = #{params.yundongId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.yundong_collection_types = #{params.yundongCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}



        and a.yundong_id IS NOT NULL


        and yundong.yundong_name like CONCAT('%',#{params.yundongName},'%')


        and yundong.yundong_types = #{params.yundongTypes}



        and yundong.yundong_video like CONCAT('%',#{params.yundongVideo},'%')


          and yundong.yundong_num >= #{params.yundongNumStart}   


          and yundong.yundong_num <= #{params.yundongNumEnd}   


        and yundong.yundong_num = #{params.yundongNum}


          and yundong.zan_number >= #{params.zanNumberStart}   


          and yundong.zan_number <= #{params.zanNumberEnd}   


        and yundong.zan_number = #{params.zanNumber}


          and yundong.cai_number >= #{params.caiNumberStart}   


          and yundong.cai_number <= #{params.caiNumberEnd}   


        and yundong.cai_number = #{params.caiNumber}


        and yundong.yundong_content like CONCAT('%',#{params.yundongContent},'%')


          and yundong.yundong_delete >= #{params.yundongDeleteStart}   


          and yundong.yundong_delete <= #{params.yundongDeleteEnd}   


        and yundong.yundong_delete = #{params.yundongDelete}


          and UNIX_TIMESTAMP(yundong.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yundong.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


order by a.${params.sort} ${params.order}
